sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var request = new sn_ws.RESTMessageV2();
    request.setEndpoint('https://adf-apr-943-test-004.lab.service-now.com/api/now/table/u_password_vault?sysparm_query=u_asset%' + current.cmdb_ci.asset_tag.toString() + '&sysparm_fields=u_password&sysparm_limit=1');
    request.setHttpMethod('GET');

    //Eg. UserName="admin", Password="admin" for this code sample.
    var user = 'admin';
    var password = 'adf-apr-943';

    request.setBasicAuth(user, password);
    request.setRequestHeader("Accept", "application/json");

    var response = request.execute();
    var httpStatus = response.getStatusCode();
    var responseBody = response.getBody();
    gs.info(response.getBody());
	gs.info(httpStatus);

//     var obj = JSON.parse(response.getBody());
//     var myPass = obj.result[0].u_password;
//     // gs.info('current.item_type :'+obj.result[0]);
//     gs.info('The admin password for your laptop is: ' + myPass);


    if (httpStatus == 200) {
        var assetJson = JSON.parser(responseBody);
		gs.info(assetJson);
        // var assetJson = new JSON.parser(responseBody);
        gs.info("The Admin Password for your Laptop is: " + assetJson.u_password);
    }

})(current, previous);
